mapper.select:
/* 배상정보 조회 :: selectComp */ SELECT A.comp_id , A.voc_id , A.code , B.code_nm , B.comp_nm , B.comp_dtl , B.refer_1 , B.refer_2 , B.refer_3 , B.refer_4 , B.refer_5 FROM logistics_system.ls_Compensation A LEFT OUTER JOIN logistics_system.ls_CompensationCode B ON A.code = B.code
AND voc_id = #{voc_id}
AND comp_id = #{comp_id}
AND code = #{code}

mapper.insert:
/* 배상정보 등록 :: insertComp */
INSERT INTO logistics_system.ls_Compensation
    (
    voc_id
    , code
    )
VALUES
    (
     #{voc_id}
    , #{code}
    )

mapper.update:
/* 배상정보 수정 :: updateComp */
UPDATE logistics_system.ls_Compensation


        code = #{code}


WHERE voc_id = #{voc_id}
  AND comp_id = #{comp_id}

mapper.delete:
/* 배상정보 삭제 :: deleteComp */
DELETE FROM logistics_system.ls_Compensation
WHERE voc_id = #{voc_id}
  AND comp_id = #{comp_id}

mapper.select:
/* 배상정보코드 조회 :: selectCompCode */ SELECT code , code_nm , comp_nm , comp_dtl , refer_1 , refer_2 , refer_3 , refer_4 , refer_5 FROM logistics_system.ls_CompensationCode
AND code = #{code}
AND code_nm = #{code_nm}
AND refer_1 = #{refer_1}

mapper.insert:
/* 배상정보코드 등록 :: insertCompCode */
INSERT INTO logistics_system.ls_CompensationCode
    (code_nm
    , comp_nm
    , comp_dtl
    , refer_1
    , refer_2
    , refer_3
    , refer_4
    , refer_5
    )
VALUES
    (#{code_nm}
    , #{comp_nm}
    , #{comp_dtl}
    , #{refer_1}
    , #{refer_2}
    , #{refer_3}
    , #{refer_4}
    , #{refer_5}
    )

mapper.update:
/* 배상정보코드 수정 :: updateCompCode */ UPDATE logistics_system.ls_CompensationCode
code_nm = #{code_nm},
comp_nm = #{comp_nm},
comp_dtl = #{comp_dtl},
refer_1 = #{refer_1},
refer_2 = #{refer_2},
refer_3 = #{refer_3},
refer_4 = #{refer_4},
refer_5 = #{refer_5}
WHERE code = #{code}

mapper.delete:
/* 배상정보코드 삭제 :: deleteCompCode */
DELETE FROM logistics_system.ls_CompensationCode
WHERE code = #{code}
