mapper.select:
/* 배상목록 조회 :: selectComp */ SELECT A.comp_info_id , A.voc_id , A.penality_id , A.comp_id , A.comp_content , A.comp_doen_yn , A.dispute_raised_yn , A.first_regist_dt , A.last_updt_dt , B.* FROM tp_Compensation A LEFT OUTER JOIN ( SELECT aa.penality_id , aa.voc_id , aa.cust_co_id , aa.driver_id , dd.driver_nm , dd.driver_mbtlnum , aa.trsp_id , cc.trsp_nm , cc.trsp_mbtlnum , aa.comp_id , aa.penality_nm , aa.penality_amt /* 패널티 금액 = 배상 금액 */ , aa.driver_chk_yn , aa.first_regist_dt , bb.comp_nm , bb.comp_dtl , bb.comp_se , bb.comp_amt , bb.comp_atc , bb.comp_rat , bb.rm , ee.voc_content , ee.atc_amt , ee.rl_dlvy_de , ee.dlvy_prearnge_de , ee.comp_reqst_yn , ee.resp_pty , ee.first_regist_dt as voc_regist_dt FROM tp_Penality aa LEFT OUTER JOIN tp_CompensationCode bb ON aa.comp_id = bb.comp_id LEFT OUTER JOIN tp_VOC ee ON aa.voc_id = ee.voc_id AND aa.cust_co_id = ee.cust_co_id AND aa.driver_id = ee.driver_id AND aa.trsp_id = ee.trsp_id LEFT OUTER JOIN tp_Transporter cc ON aa.trsp_id = cc.trsp_id LEFT OUTER JOIN tp_Driver dd ON aa.driver_id = dd.driver_id AND cc.trsp_id = dd.trsp_id ) B ON A.penality_id = B.penality_id AND A.voc_id = B.voc_id AND A.comp_id = B.comp_id
AND A.voc_id = #{voc_id}
AND A.comp_id = #{comp_id}
AND A.penality_id = #{penality_id}

mapper.insert:
/* 배상정보 등록 :: insertComp */
INSERT INTO tp_Compensation
    (
      voc_id
    , penality_id
    , comp_id
    , comp_content
    , comp_doen_yn
    , dispute_raised_yn
    , first_regist_dt
    , last_updt_dt
    )
VALUES
    (
     #{voc_id}
    , #{penality_id}
    , #{comp_id}
    , #{comp_content}
    , #{comp_doen_yn}
    , #{dispute_raised_yn}
    , NOW()
    , NOW()
    )

mapper.update:
/* 배상정보 수정 :: updateComp */
UPDATE tp_Compensation


        voc_id = #{voc_id}


        penality_id = #{penality_id}


        comp_id = #{comp_id}


        comp_content = #{comp_content}


        comp_doen_yn = #{comp_doen_yn}


        comp_doen_yn = #{comp_doen_yn}

    last_updt_dt = NOW()

WHERE comp_info_id = #{comp_info_id}

mapper.delete:
/* 배상정보 삭제 :: deleteComp */
DELETE FROM tp_Compensation
WHERE comp_info_id = #{comp_info_id}

mapper.select:
/* 배상정보코드 조회 :: selectCompCode */ SELECT comp_id , comp_nm , comp_dtl , comp_se , comp_amt , comp_rat , rm , first_regist_dt , last_updt_dt FROM tp_CompensationCode
AND comp_id = #{comp_id}
AND code_nm = #{code_nm}
AND comp_se = #{comp_se}

mapper.insert:
/* 배상정보코드 등록 :: insertCompCode */
INSERT INTO tp_CompensationCode
    (
      comp_nm
    , comp_dtl
    , comp_se
    , comp_amt
    , comp_rat
    , rm
    , first_regist_dt
    , last_updt_dt
    )
VALUES
    (
      #{comp_nm}
    , #{comp_dtl}
    , #{comp_se}
    , #{comp_amt}
    , #{comp_rat}
    , #{rm}
    , NOW()
    , NOW()
    )

mapper.delete:
/* 배상정보코드 삭제 :: deleteCompCode */
DELETE FROM tp_CompensationCode
WHERE comp_id = #{comp_id}

mapper.select:
/* 배상정보코드 사용여부 :: selectCompCodeUseAt */
SELECT comp_id
FROM tp_Penality
WHERE comp_id = #{comp_id}
