mapper.select:
/* 고객사 조회 :: selectCust */ SELECT cust_co_id , cust_co_nm , other_info , cust_mbtlnum , first_regist_dt , last_updt_dt FROM tp_Customer
AND cust_co_id = #{cust_co_id}
AND cust_co_nm = #{cust_co_nm}
AND cust_mbtlnum LIKE CONCAT('%', #{cust_co_nm}, '%')

mapper.insert:
/* 고객사 등록 :: insertCust */
INSERT INTO tp_Customer
    (
     cust_co_nm
     , other_info
     , cust_mbtlnum
     , use_yn
     , first_regist_dt
     , last_updt_dt
     )
VALUES
    (
     #{cust_co_nm}
     , #{other_info}
    , #{cust_mbtlnum}
    , #{use_yn}
    , NOW()
    , NOW()
     )

mapper.delete:
/* 고객사 삭제 :: deleteCust */
DELETE FROM tp_Customer
WHERE cust_co_id = #{cust_co_id}
