mapper.select:
/* 패널티 조회 :: selectPenality */
SELECT penality_id
     , voc_id
     , penality_amt
     , driver_chk_yn
FROM logistics_system.ls_Penality


        AND penality_id = #{penality_id}


        AND voc_id = #{voc_id}


        AND driver_chk_yn = #{driver_chk_yn}

mapper.insert:
/* 패널티 등록 :: insertPenality */
INSERT INTO logistics_system.ls_Penality
    (
     voc_id
     , penality_amt
     , driver_chk_yn
     )
VALUES
    (
     #{voc_id}
    , #{penality_amt}
    , #{driver_chk_yn}
    )

mapper.update:
/* 패널티 수정 :: updatePenality */
UPDATE logistics_system.ls_Penality


        voc_id = #{voc_id},


        penality_amt = #{penality_amt},


        driver_chk_yn = #{driver_chk_yn}


WHERE penality_id = #{penality_id}

mapper.delete:
/* 패널티 삭제 :: deletePenality */
DELETE FROM logistics_system.ls_Penality
WHERE penality_id = #{penality_id}
