mapper.select:
/* 패널티 조회 :: selectPenality */ SELECT A.penality_id , A.voc_id , A.cust_co_id , A.driver_id , A.trsp_id , A.comp_id , A.penality_nm , A.penality_amt , A.driver_chk_yn , A.first_regist_dt , A.last_updt_dt FROM tp_Penality A
AND A.penality_id = #{penality_id}
AND A.voc_id = #{voc_id}
AND A.driver_chk_yn = #{driver_chk_yn}

mapper.insert:
/* 패널티 등록 :: insertPenality */
INSERT INTO tp_Penality
    (
       voc_id
     , cust_co_id
     , driver_id
     , trsp_id
     , comp_id
     , penality_nm
     , penality_amt
     , driver_chk_yn
     , first_regist_dt
     , last_updt_dt
     )
VALUES
    (
     #{voc_id}
    , #{cust_co_id}
    , #{driver_id}
    , #{trsp_id}
    , #{comp_id}
    , #{penality_nm}
    , #{penality_amt}
    , #{driver_chk_yn}
    , NOW()
    , NOW()
    )

mapper.delete:
/* 패널티 삭제 :: deletePenality */
DELETE FROM tp_Penality
WHERE penality_id = #{penality_id}
