mapper.select:
/* 운송사 조회 :: selectTrsp */
SELECT trsp_id
     , trsp_nm
     , other_info
FROM logistics_system.ls_Transporter


        AND trsp_id = #{trsp_id}


        AND trsp_nm = #{trsp_nm}

mapper.insert:
/* 운송사 등록 :: insertTrsp */
INSERT INTO logistics_system.ls_Transporter
    (
     trsp_nm
     , other_info
     )
VALUES
    (
     #{trsp_nm}
    , #{other_info}
     )

mapper.update:
/* 운송사 수정 :: updateTrsp */
UPDATE logistics_system.ls_Transporter


        trsp_nm = #{trsp_nm},


        other_info = #{other_info}


WHERE trsp_id = #{trsp_id}

mapper.delete:
/* 운송사 삭제 :: deleteTrsp */
DELETE FROM logistics_system.ls_Transporter
WHERE trsp_id = #{trsp_id}
