mapper.select:
/* VOC 조회 :: selectVoc */
SELECT voc_id
     , content
     , resp_pty
     , comp_doen_yn
     , dispute_raised_yn
     , cust_co_id
     , trsp_id
FROM logistics_system.ls_VOC


        AND voc_id = #{voc_id}


        AND resp_pty = #{resp_pty}


        AND comp_doen_yn = #{comp_doen_yn}


        AND cust_co_id = #{cust_co_id}


        AND trsp_id = #{trsp_id}

mapper.insert:
/* VOC 등록 :: insertVoc */
INSERT INTO logistics_system.ls_VOC
    (
     content
    , resp_pty
    , comp_doen_yn
    , dispute_raised_yn
    , cust_co_id
    , trsp_id
    )
VALUES
    (
     #{content}
    , #{resp_pty}
    , #{comp_doen_yn}
    , #{dispute_raised_yn}
    , #{cust_co_id}
    , #{trsp_id}
    )

mapper.update:
/* VOC 수정 :: updateVoc */
UPDATE logistics_system.ls_VOC


        content = #{content},


        resp_pty = #{resp_pty},


        comp_doen_yn = #{comp_doen_yn},


        dispute_raised_yn = #{dispute_raised_yn},


        cust_co_id = #{cust_co_id},


        trsp_id = #{trsp_id}


WHERE voc_id = #{voc_id}

mapper.delete:
/* VOC 삭제 :: deleteVoc */
DELETE FROM logistics_system.ls_VOC
WHERE voc_id = #{voc_id}
